import React, { useState } from 'react';
import styled from 'styled-components';


const NewCardContainer = styled.section`
  .game-card {
    margin-bottom: 30px;
    width: 900px;
    height: 500px;
    background: #343434 no-repeat center center / cover;
    border-radius: 16px;
    overflow: hidden;
    position: relative;
    cursor: pointer;
    transition: all 1s ease-in-out;
  }

  .item-desc {
    padding: 0 24px 12px;
    color: #fff;
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    background: linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, 1));
    transform: translateY(100%);
    transition: transform 1s ease-in-out; /* Adjusted transition property */
    overflow: hidden; /* Hide overflow */
    max-height: 50px; /* Initially collapsed */
    opacity: 0; /* Initially transparent */
  }

  .game-card.active .item-desc {
    transform: translateY(0);
    max-height: 400px; /* Adjust height */
    opacity: 1;
    transition-timing-function: cubic-bezier(0.275, 0.885, 0.32, 1.275); /* Smooth transition */
  }

  .item-desc p {
    margin-bottom: 10px; /* Add spacing between paragraphs */
    margin-right : 10px ;
    color: #fff; /* Set paragraph text color to white */
  }

  .read-more {
    color: #e73700;
    cursor: pointer;
    display: block;
    margin-top: 10px;
    text-decoration: underline;
  }

  /* Date styling */
  .game-card-date {
    position: absolute;
    top: 10px;
    left: 10px;
    color: #000;
    font-size: 14px;
    font-weight: bold;
    background-color: rgba(255, 255, 255, 0.7);
    padding: 5px 10px;
    border-radius: 5px;
  }
`;

const  BreakingNews = ({ data }) => {
    const [isActive, setIsActive] = useState(false);
  
    const toggleCard = () => {
      setIsActive(!isActive);
    };
  
    const [isActive2, setIsActive2] = useState(false);
  
    const toggleCard2 = () => {
      console.log("changing");
      setIsActive2(!isActive2);
    };
  
  
    return (
      <>
      <NewCardContainer>
        <div
          className={`game-card ${isActive ? 'active' : ''}`}
          style={{ backgroundImage: `url(${data.image})` }}
          onClick={toggleCard}
        >
          <span className="game-card-date">{data.date}</span>
          <div className="item-desc">
            <h3 style={{ color: '#fff' }}>{data.title}</h3>
            <p>{isActive ? data.summary : ''}</p>
            <a className="read-more" onClick={(event) => {
              event.stopPropagation();
              toggleCard2();
            }}>
              Read more
            </a>
          </div>
        </div>
      
        <CardModal data={data} isActive={isActive2} onClose={toggleCard2} />
        </NewCardContainer></>
    );
  };

export default BreakingNews;
