import React, { useEffect, useState } from 'react'
import Navbar from './Component/Navbar'
import './App.css'; 
import BreakingNews from './Component/BreakingNews';
import TopHeadlines from './Component/HeadLines';
import News from './Component/News';
function App() {
  const [currentTopic,setCurrentTopic]=useState('politics')
  const [headlinesData , setHeadlineData] = useState([])
  const [selectedNews,setSelectedNews]=useState([])
  const handleTopicChange = (newTopic) => {
    setCurrentTopic(newTopic);
    console.log("changeddd");
    // console.log(currentTopic);
  };

  useEffect(() => {
    fetch("/api/general")
      .then(response => response.json())
      .then(data => {
        setHeadlineData(data);
      })
      .catch(error => {
        console.error('Error fetching general data:', error);
      });

    fetch(`/api/${currentTopic}`)
      .then(response => response.json())
      .then(data => {
        setSelectedNews(data);
        console.log("Fetched politics data:", data); // Log fetched politics data
      })
      .catch(error => {
        console.error('Error fetching politics data:', error);
      });
  }, [currentTopic]); 



  const news = {
    image: 'https://www.yudiz.com/codepen/expandable-animated-card-slider/dota-2.jpg',
    title: 'Lorem Ipsum Dolor Sit Amet',
    date: 'May 6, 2024',
    summary : "Sure, here are 100 words in a sentence:The diligent squirrel, with a bushy tail twitching, scampered swiftly across the dewy grass, leaping gracefully from branch to branch, as the golden sun peeked through the emerald canopy above, casting dappled shadows upon the forest floor, where tiny flowers bloomed shyly amidst the tangled undergrowth, their delicate petals kissed by the gentle breeze that whispered secrets to the swaying trees, while distant bird songs filled the air with melodies of joy, and the murmuring stream meandered peacefully, reflecting the azure sky like a shimmering mirror, in this enchanted realm where nature's beauty danced in perfect harmony."
  }




  return (
      <div className='App'>
        <Navbar currentTopic={currentTopic} onTopicChange={handleTopicChange} />
        <h1> $  $</h1>
        <div className="content-container">
          <div className="topnews-container">
            <div className="newcard">
              <h1>Breaking News</h1>
              <BreakingNews data={headlinesData[0]}/>
            </div>
            <div className="top-headlines">
              <h1>Top Head Lines</h1>
              <TopHeadlines data={headlinesData}/>
            </div>
          </div>




          <News data={selectedNews}/>


        </div>

      </div>
    );
  }

export default App
