import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome,faNewspaper, faFutbol, faBalanceScale } from '@fortawesome/free-solid-svg-icons';
import './css/Navbar.css'; // Import CSS file for styling
const Navbar = ({ currentTopic, onTopicChange }) => {
    
   
    return (
        <nav className={ "navbar dark-mode"}>
            <div className="logo">
                <FontAwesomeIcon icon={faNewspaper} className="newspaper-icon" />
                <span>Today's News</span>
            </div>
    
            <div className="nav-links">
                <a href="#"><FontAwesomeIcon icon={faHome} /> Home</a>
                <button onClick={() => onTopicChange('ipl')}> Sports</button>
                <button onClick={() => onTopicChange('politics')}>Politics</button>
                <button onClick={() => onTopicChange('ipl')}>  <FontAwesomeIcon icon={faFutbol} /> Sports </button>
                <button onClick={() => onTopicChange('politics')}> <FontAwesomeIcon icon={faBalanceScale} /> Politics </button>
            </div>
           
        </nav>
    );
}

export default Navbar;
