import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faInfoCircle, faSearch, faGlobe, faNewspaper } from '@fortawesome/free-solid-svg-icons';
import './css/Navbar.css'; // Import CSS file for styling
const Navbar = ({ currentTopic, onTopicChange }) => {
    // const [isDarkMode, setIsDarkMode] = useState(false);
    // const toggleDarkMode = () => setIsDarkMode(!isDarkMode);
   
    return (
        <nav className={ "navbar dark-mode"}>
            <div className="logo">
                <FontAwesomeIcon icon={faNewspaper} className="newspaper-icon" />
                <span>Today's News</span>
            </div>
            
            <div className="search-bar">
                <input type="text" placeholder="Search..." />
                <FontAwesomeIcon icon={faSearch} className="search-icon" />
            </div>
            <div className="nav-links">
                <a href="#"><FontAwesomeIcon icon={faHome} /> Home</a>
                <a href="#"><FontAwesomeIcon icon={faInfoCircle} /> About</a>
                <button onClick={() => onTopicChange('ipl')}> Sports</button>
      <button onClick={() => onTopicChange('politics')}>Politics</button>
            </div>
            <div className="dark-mode-toggle" onClick={toggleDarkMode}>
                {isDarkMode ? <span>Light Mode</span> : <span>Dark Mode</span>}
            </div>
        </nav>
    );
}

export default Navbar;
