// TopHeadlines.js
import React, { useState } from 'react';
import './css/HeadLines.css' ;
import CardModal from './Modal/CardModal';
const TopHeadlines = ({ data }) => {
    const [modalData, setModalData] = useState(null); // State to store data for modal
  
    const handleImageClick = (item) => {
      setModalData(item); // Set data for modal when image is clicked
    };
  
    return (
      <div className="top-headlines-wrapper">
        <div className="top-headlines-container">
          
          {data.map((item, index) => (
            <div className="headline-item" key={index}>
              <div className="image-container" onMouseEnter={() => handleImageClick(item)}>
                <img src={item.urlToImage} alt={item.title} />
              </div>
              <div className="summary">
                <h3>{item.title}</h3>
                <p>{item.summary}</p>
              </div>
            </div>
          ))}
        </div>
        {/* Render CardModal if modalData is not null */}
        {modalData && <CardModal data={modalData} isActive={true} onClose={() => setModalData(null)} />}
      </div>
    );
  };
  
  export default TopHeadlines;
